pan> <span style=\"color: #21439c;\">affiche</span>(){</span> <span style=\"color: #3b3b3b;\">        echo <span style=\"color: #666666;\">\"je suis une voiture de marque \"</span><span style=\"color: #006699;\"><strong>.</strong></span><span style=\"color: #0053ff;\"><strong>$this</strong></span><span style=\"color: #006699;\"><strong>-></strong></span>marque<span style=\"color: #006699;\"><strong>.</strong></span><span style=\"color: #666666;\">\" et de modele \"</span><span style=\"color: #006699;\"><strong>.</strong></span><span style=\"color: #0053ff;\"><strong>$this</strong></span><span style=\"color: #006699;\"><strong>-></strong></span>modele;</span> <span style=\"color: #3b3b3b;\">        echo <span style=\"color: #666666;\">\"<br ?-->\"</span>;</span> <span style=\"color: #3b3b3b;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"color: #0053ff;\"><strong>$i</strong></span><span style=\"color: #006699;\"><strong>=</strong></span><span style=\"color: #a8017e;\">1</span>;</span> <span style=\"color: #3b3b3b;\"><span style=\"color: #006699;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"color: #006699;\"><strong>foreach</strong></span>(<span style=\"color: #0053ff;\"><strong>$this</strong></span><span style=\"color: #006699;\"><strong>-&gt;</strong></span>roues <span style=\"color: #006699;\"><strong>as</strong></span> <span style=\"color: #0053ff;\"><strong>$element</strong></span>){</span> <span style=\"color: #3b3b3b;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;echo <span style=\"color: #0053ff;\"><strong>$i</strong></span><span style=\"color: #006699;\"><strong>.</strong></span><span style=\"color: #666666;\">\" : \"</span><span style=\"color: #006699;\"><strong>.</strong></span><span style=\"color: #0053ff;\"><strong>$element</strong></span>;</span> <span style=\"color: #3b3b3b;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"color: #0053ff;\"><strong>$i</strong></span><span style=\"color: #006699;\"><strong>++</strong></span>;</span> <span style=\"color: #3b3b3b;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}</span> <span style=\"color: #3b3b3b;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;echo <span style=\"color: #666666;\">\"<br /><br /><br />\"</span>;</span> <span style=\"color: #3b3b3b;\">&nbsp;&nbsp;&nbsp;&nbsp;} </span> <span style=\"color: #3b3b3b;\">}</span></span></code></pre>\r\n<p>&nbsp;</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-family: Frutiger;\">Index.php</span></p>\r\n<table width=\"639\" cellspacing=\"0\" cellpadding=\"0\"><colgroup><col width=\"17\" /> <col width=\"622\" /> </colgroup>\r\n<tbody>\r\n<tr>\r\n<td style=\"border: none; padding: 0cm;\" width=\"17\">\r\n<p><span style=\"font-family: Frutiger;\"><span style=\"font-size: small;\">1</span></span></p>\r\n<p><span style=\"font-family: Frutiger;\"><span style=\"font-size: small;\">2</span></span></p>\r\n<p><span style=\"font-family: Frutiger;\"><span style=\"font-size: small;\">3</span></span></p>\r\n<p><span style=\"font-family: Frutiger;\"><span style=\"font-size: small;\">4</span></span></p>\r\n<p><span style=\"font-family: Frutiger;\"><span style=\"font-size: small;\">5</span></span></p>\r\n<p><span style=\"font-family: Frutiger;\"><span style=\"font-size: small;\">6</span></span></p>\r\n<p><span style=\"font-family: Frutiger;\"><span style=\"font-size: small;\">7</span></span></p>\r\n<p><span style=\"font-family: Frutiger;\"><span style=\"font-size: small;\">8</span></span></p>\r\n<p><span style=\"font-family: Frutiger;\"><span style=\"font-size: small;\">9</span></span></p>\r\n<p><span style=\"font-family: Frutiger;\"><span style=\"font-size: small;\">10</span></span></p>\r\n<p><span style=\"font-family: Frutiger;\"><span style=\"font-size: small;\">11</span></span></p>\r\n<p><span style=\"font-family: Frutiger;\"><span style=\"font-size: small;\">12</span></span></p>\r\n<p>&nbsp;</p>\r\n</td>\r\n<td style=\"border: none; padding: 0cm;\" width=\"622\">\r\n<pre class=\"western\" style=\"background: #ffffff;\"><code class=\"western\"><span style=\"color: #3b3b3b;\"><span style=\"font-family: Frutiger;\"><span style=\"font-size: small;\"><!--?php </span></span></span></code> <span style=\"color: #3b3b3b;\"><span style=\"color: #006699;\"><strong>include_once</strong></span>(<span style=\"color: #666666;\">\'voiture.class.php\'</span>);</span> <span style=\"color: #3b3b3b;\"><span style=\"color: #006699;\"><strong>include_once</strong></span>(<span style=\"color: #666666;\">\'roue.class.php\'</span>);</span> <span style=\"color: #3b3b3b;\"><span style=\"color: #0053ff;\"><strong>$carTest</strong></span> <span style=\"color: #006699;\"><strong>=</strong></span> <span style=\"color: #006699;\"><strong>new</strong></span> <span style=\"color: #a535ae;\">Voiture</span>(<span style=\"color: #666666;\">\"Renault\"</span>,<span style=\"color: #666666;\">\"Scenic\"</span>);</span> <span style=\"color: #3b3b3b;\"><span style=\"color: #0053ff;\"><strong>$carTest</strong></span><span style=\"color: #006699;\"><strong>-></strong></span>affiche();</span> <span style=\"color: #3b3b3b;\"><span style=\"color: #0053ff;\"><strong>$r1</strong></span> <span style=\"color: #006699;\"><strong>=</strong></span> <span style=\"color: #006699;\"><strong>new</strong></span> <span style=\"color: #a535ae;\">Roue</span>(<span style=\"color: #666666;\">\"dunlop\"</span>,<span style=\"color: #a8017e;\">60</span>);</span> <span style=\"color: #3b3b3b;\"><span style=\"color: #0053ff;\"><strong>$r2</strong></span> <span style=\"color: #006699;\"><strong>=</strong></span> <span style=\"color: #006699;\"><strong>new</strong></span> <span style=\"color: #a535ae;\">Roue</span>(<span style=\"color: #666666;\">\"michelin\"</span>,<span style=\"color: #a8017e;\">80</span>);</span> <span style=\"color: #3b3b3b;\"><span style=\"color: #0053ff;\"><strong>$carTest</strong></span>→addRoue(<span style=\"color: #0053ff;\"><strong>$r1</strong></span>);</span> <span style=\"color: #3b3b3b;\"><span style=\"color: #0053ff;\"><strong>$carTest</strong></span><span style=\"color: #006699;\"><strong>-></strong></span>addRoue(<span style=\"color: #0053ff;\"><strong>$r1</strong></span>);</span> <span style=\"color: #3b3b3b;\"><span style=\"color: #0053ff;\"><strong>$carTest</strong></span>→addRoue(<span style=\"color: #0053ff;\"><strong>$r2</strong></span>);</span> <span style=\"color: #3b3b3b;\"><span style=\"color: #0053ff;\"><strong>$carTest</strong></span><span style=\"color: #006699;\"><strong>-></strong></span>addRoue(<span style=\"color: #0053ff;\"><strong>$r2</strong></span>);</span> <span style=\"color: #0053ff;\"><strong>$carTest→affiche();</strong></span></pre>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h2 class=\"western\"> </h2>\r\n<h2 class=\"western\"><a name=\"conclusion\"></a><span style=\"font-family: Frutiger;\">Conclusion</span></h2>\r\n<p><span style=\"font-family: Frutiger;\">Vous l’aurez compris, la programmation orientée objet c’est aussi complexe que puissant. Une fois les base de l’OOP maîtrisées, vous pourrez commencer à aller plus loin dans l’architecture logicielle, la programmation par composants, les design-patterns etc.</span></p>\r\n<p><span style=\"font-family: Frutiger;\">De nos jours, la plupart des infrastructure logicielles importantes ou complexes sont codées en Objet, ce n’est pas un hasard. La </span><strong><span style=\"font-family: Frutiger;\">simplicité</span></strong><span style=\"font-family: Frutiger;\"> grâce à l’encapsulation, la </span><strong><span style=\"font-family: Frutiger;\">réutilisabilité</span></strong><span style=\"font-family: Frutiger;\"> grâce à l’héritage et la </span><strong><span style=\"font-family: Frutiger;\">généricité</span></strong><span style=\"font-family: Frutiger;\"> grâce au polymorphisme expliquent à elles trois </span><span style=\"font-family: Frutiger;\">l’engouement</span><span style=\"font-family: Frutiger;\"> persistant pour les langages objets et leur large domination parmi les langages de programmation.</span></p>\r\n<p><span style=\"font-family: Frutiger;\">Vous verrez, une fois habitué, penser objet vous sera naturel.</span></p>\r\n<h2 class=\"western\"><span style=\"font-family: Frutiger;\">Autre site à voir :</span></h2>\r\n<h4 class=\"western\"><span style=\"font-family: Frutiger;\">PHP : des cours et des cours ....</span></h4>\r\n<ul>\r\n<li>\r\n<p style=\"margin-bottom: 0cm;\"><a href=\"https://openclassrooms.com/courses/concevez-votre-site-web-avec-php-et-mysql/session-cookies\"><span style=\"font-family: Frutiger;\">Les sessions et les cookies : https://openclassrooms.com/courses/concevez-votre-site-web-avec-php-et-mysql/session-cookies</span></a></p>\r\n</li>\r\n<li>\r\n<p style=\"margin-bottom: 0cm;\"><a href=\"https://openclassrooms.com/courses/pdo-comprendre-et-corriger-les-erreurs-les-plus-frequentes\"><span style=\"font-family: Frutiger;\">Paramétrer PDO et premier pas en POO : https://openclassrooms.com/courses/pdo-comprendre-et-corriger-les-erreurs-les-plus-frequentes</span></a></p>\r\n</li>\r\n<li>\r\n<p style=\"margin-bottom: 0cm;\"><a href=\"http://sylvie-vauthier.developpez.com/tutoriels/php/grand-debutant/\"><span style=\"font-family: Frutiger;\">Pour débutants pressés : http://sylvie-vauthier.developpez.com/tutoriels/php/grand-debutant/</span></a></p>\r\n</li>\r\n<li>\r\n<p style=\"margin-bottom: 0cm;\"><a href=\"http://g-rossolini.developpez.com/tutoriels/php/cours/\"><span style=\"font-family: Frutiger;\">Et un cours complet PHP : http://g-rossolini.developpez.com/tutoriels/php/cours/</span></a></p>\r\n</li>\r\n<li>\r\n<p style=\"margin-bottom: 0cm;\"><span style=\"font-family: Frutiger;\">Des tutaux vidéo de \"mackup\" pour les bases du PHP :<a href=\"https://www.youtube.com/watch?v=-UFIFbD2-sw&list=PLbLDQI0AILjIKXJLwu8X9SfhmklWpVhUz\"><br ?-->https://www.youtube.com/watch?v=-UFIFbD2-sw&amp;list=PLbLDQ